DROP TABLE IF EXISTS cms_article
CREATE TABLE `cms_article` (  `id` int(11) NOT NULL AUTO_INCREMENT COMMENT '文章ID',  `cid` int(11) NOT NULL COMMENT '所属栏目ID',  `title` varchar(200) NOT NULL COMMENT '标题',  `subtitle` varchar(200) DEFAULT NULL,  `att` set('a','b','c','d','e','f','g') DEFAULT NULL COMMENT '属性',  `pic` varchar(200) DEFAULT NULL COMMENT '缩略图',  `pic_info` varchar(500) DEFAULT NULL,  `pic02` varchar(500) DEFAULT NULL,  `pic02_info` varchar(500) DEFAULT NULL,  `pic03` varchar(500) DEFAULT NULL,  `pic03_info` varchar(500) DEFAULT NULL,  `source` text COMMENT '来源',  `author` text COMMENT '作者',  `resume` varchar(500) DEFAULT NULL COMMENT '摘要',  `pubdate` varchar(40) NOT NULL COMMENT '发表日期',  `content` text COMMENT '文章内容',  `hits` int(11) NOT NULL DEFAULT '0' COMMENT '点击次数',  `orderlist` int(11) NOT NULL DEFAULT '0',  `created_by` int(11) NOT NULL COMMENT '创建者',  `created_date` datetime NOT NULL COMMENT '创建时间',  `delete_session_id` int(11) DEFAULT NULL COMMENT '删除人ID',  PRIMARY KEY (`id`)) ENGINE=MyISAM AUTO_INCREMENT=17 DEFAULT CHARSET=utf8
DROP TABLE IF EXISTS cms_cart
CREATE TABLE `cms_cart` (  `id` int(11) NOT NULL AUTO_INCREMENT COMMENT 'ID',  `ordernum` varchar(255) NOT NULL,  `productid` int(11) NOT NULL,  `title` varchar(255) NOT NULL,  `pic` varchar(255) NOT NULL,  `price` varchar(255) NOT NULL,  `num` varchar(255) NOT NULL,  `userid` int(11) NOT NULL,  PRIMARY KEY (`id`)) ENGINE=MyISAM DEFAULT CHARSET=utf8
DROP TABLE IF EXISTS cms_case
CREATE TABLE `cms_case` (  `id` int(11) NOT NULL AUTO_INCREMENT COMMENT '文章ID',  `cid` int(11) NOT NULL COMMENT '所属栏目ID',  `title` varchar(200) NOT NULL COMMENT '标题',  `subtitle` varchar(200) DEFAULT NULL,  `att` varchar(255) DEFAULT NULL COMMENT '属性',  `pic` varchar(200) DEFAULT NULL COMMENT '缩略图',  `pic_big` varchar(200) DEFAULT NULL,  `price` varchar(200) DEFAULT '0' COMMENT '来源',  `brand` varchar(300) DEFAULT NULL COMMENT '作者',  `resume` varchar(500) DEFAULT NULL COMMENT '摘要',  `purpose` text,  `subsidiary` text,  `pubdate` varchar(40) NOT NULL COMMENT '发表日期',  `content` text COMMENT '文章内容',  `hits` int(11) NOT NULL DEFAULT '0' COMMENT '点击次数',  `color` varchar(500) DEFAULT NULL,  `beizhu` text,  `infor01` varchar(500) DEFAULT NULL,  `infor02` varchar(500) DEFAULT NULL,  `infor03` varchar(500) DEFAULT NULL,  `infor04` varchar(500) DEFAULT NULL,  `infor05` varchar(500) DEFAULT NULL,  `infor06` varchar(500) DEFAULT NULL,  `infor07` varchar(500) DEFAULT NULL,  `infor08` varchar(500) DEFAULT NULL,  `infor09` varchar(500) DEFAULT NULL,  `orderlist` int(11) DEFAULT NULL,  `created_by` int(11) NOT NULL COMMENT '创建者',  `created_date` datetime NOT NULL COMMENT '创建时间',  `delete_session_id` int(11) DEFAULT NULL COMMENT '删除人ID',  PRIMARY KEY (`id`)) ENGINE=MyISAM DEFAULT CHARSET=utf8
DROP TABLE IF EXISTS cms_case_category
CREATE TABLE `cms_case_category` (  `id` int(11) NOT NULL AUTO_INCREMENT COMMENT '栏目ID',  `pid` int(11) NOT NULL DEFAULT '0' COMMENT '父栏目ID',  `name` varchar(50) NOT NULL COMMENT '栏目名称',  `description` text,  `seq` int(11) NOT NULL DEFAULT '0' COMMENT '栏目排序',  PRIMARY KEY (`id`)) ENGINE=MyISAM DEFAULT CHARSET=utf8
DROP TABLE IF EXISTS cms_category
CREATE TABLE `cms_category` (  `id` int(11) NOT NULL AUTO_INCREMENT COMMENT '栏目ID',  `pid` int(11) NOT NULL DEFAULT '0' COMMENT '父栏目ID',  `name` varchar(50) NOT NULL COMMENT '栏目名称',  `description` text,  `seq` int(11) NOT NULL DEFAULT '0' COMMENT '栏目排序',  PRIMARY KEY (`id`)) ENGINE=MyISAM AUTO_INCREMENT=3 DEFAULT CHARSET=utf8
DROP TABLE IF EXISTS cms_comment
CREATE TABLE `cms_comment` (  `id` int(11) NOT NULL AUTO_INCREMENT COMMENT 'ID',  `article_id` int(11) NOT NULL,  `userid` int(11) NOT NULL DEFAULT '0',  `username` varchar(255) NOT NULL,  `email` varchar(255) NOT NULL,  `title` varchar(200) NOT NULL,  `content` text NOT NULL,  `reply` text NOT NULL,  `addtime` varchar(255) NOT NULL,  `replytime` varchar(255) NOT NULL,  `state` int(11) NOT NULL DEFAULT '0' COMMENT '状态（1 发布 0 禁用）',  PRIMARY KEY (`id`)) ENGINE=MyISAM DEFAULT CHARSET=utf8
DROP TABLE IF EXISTS cms_file
CREATE TABLE `cms_file` (  `id` int(11) NOT NULL AUTO_INCREMENT,  `filename` varchar(200) DEFAULT NULL,  `ffilename` varchar(200) DEFAULT NULL,  `path` varchar(250) DEFAULT NULL,  `ext` varchar(10) DEFAULT NULL,  `size` int(11) DEFAULT NULL,  `upload_date` datetime DEFAULT NULL,  PRIMARY KEY (`id`)) ENGINE=MyISAM AUTO_INCREMENT=96 DEFAULT CHARSET=utf8
DROP TABLE IF EXISTS cms_friendlink
CREATE TABLE `cms_friendlink` (  `id` int(11) NOT NULL AUTO_INCREMENT COMMENT 'ID',  `category` varchar(100) NOT NULL,  `name` varchar(200) NOT NULL COMMENT '网站名称',  `url` varchar(200) NOT NULL COMMENT '网址',  `description` varchar(400) NOT NULL COMMENT '站点简介',  `pic` varchar(500) NOT NULL COMMENT '网站LOGO',  `seq` int(11) NOT NULL DEFAULT '0' COMMENT '排列顺序',  PRIMARY KEY (`id`)) ENGINE=MyISAM AUTO_INCREMENT=52 DEFAULT CHARSET=utf8
DROP TABLE IF EXISTS cms_message
CREATE TABLE `cms_message` (  `id` int(11) NOT NULL AUTO_INCREMENT COMMENT 'ID',  `title` varchar(200) DEFAULT NULL,  `pic` varchar(500) DEFAULT NULL,  `province` varchar(100) DEFAULT NULL,  `city` varchar(500) DEFAULT NULL,  `company` varchar(500) DEFAULT NULL,  `inquiry` varchar(500) DEFAULT NULL,  `content` text,  `ip` varchar(20) DEFAULT NULL COMMENT '留言人IP',  `addtime` varchar(200) DEFAULT NULL COMMENT '留言日期',  `validate` int(11) NOT NULL DEFAULT '0',  PRIMARY KEY (`id`)) ENGINE=MyISAM AUTO_INCREMENT=15 DEFAULT CHARSET=utf8
DROP TABLE IF EXISTS cms_notice
CREATE TABLE `cms_notice` (  `id` int(11) NOT NULL AUTO_INCREMENT COMMENT 'ID',  `title` varchar(200) NOT NULL COMMENT '公告标题',  `titleen` varchar(500) NOT NULL,  `titlejp` varchar(500) NOT NULL,  `content` text NOT NULL COMMENT '公告内容',  `pic_url` varchar(500) NOT NULL,  `category` varchar(255) NOT NULL,  `state` int(11) DEFAULT NULL,  PRIMARY KEY (`id`)) ENGINE=MyISAM DEFAULT CHARSET=utf8
DROP TABLE IF EXISTS cms_order
CREATE TABLE `cms_order` (  `id` int(11) NOT NULL AUTO_INCREMENT COMMENT 'ID',  `username` varchar(200) DEFAULT NULL,  `sex` varchar(100) NOT NULL,  `email` varchar(500) DEFAULT NULL,  `province` varchar(100) DEFAULT NULL,  `city` varchar(500) DEFAULT NULL,  `company` varchar(500) DEFAULT NULL,  `qualifications` varchar(500) DEFAULT NULL,  `years` varchar(100) NOT NULL,  `position` varchar(500) NOT NULL,  `academic` text,  `ip` varchar(20) DEFAULT NULL COMMENT '留言人IP',  `addtime` varchar(200) DEFAULT NULL COMMENT '留言日期',  `validate` int(11) NOT NULL DEFAULT '0',  PRIMARY KEY (`id`)) ENGINE=MyISAM DEFAULT CHARSET=utf8
DROP TABLE IF EXISTS cms_page
CREATE TABLE `cms_page` (  `id` int(11) NOT NULL AUTO_INCREMENT COMMENT 'ID',  `code` varchar(20) DEFAULT NULL COMMENT '别名',  `title` varchar(100) DEFAULT NULL COMMENT '名称',  `content` text COMMENT '内容',  `created_date` datetime DEFAULT NULL COMMENT '创建日期',  PRIMARY KEY (`id`)) ENGINE=MyISAM DEFAULT CHARSET=utf8
DROP TABLE IF EXISTS cms_preordain
CREATE TABLE `cms_preordain` (  `id` int(11) NOT NULL AUTO_INCREMENT COMMENT 'ID',  `code` varchar(255) NOT NULL,  `country` varchar(255) NOT NULL,  `company` varchar(255) NOT NULL,  `username` varchar(200) NOT NULL,  `tele` varchar(255) NOT NULL,  `num01` varchar(255) NOT NULL,  `num02` varchar(255) NOT NULL,  `num03` varchar(255) NOT NULL,  `num04` varchar(10) NOT NULL,  `num05` varchar(50) NOT NULL,  `num06` varchar(500) NOT NULL,  `num07` varchar(500) NOT NULL,  `start_y` varchar(20) NOT NULL,  `start_m` varchar(20) NOT NULL,  `start_d` varchar(20) NOT NULL,  `end_y` varchar(255) NOT NULL,  `end_m` varchar(255) NOT NULL,  `end_d` varchar(255) NOT NULL,  `addtime` varchar(255) NOT NULL,  `state` int(11) NOT NULL DEFAULT '1' COMMENT '状态（1 发布 0 禁用）',  PRIMARY KEY (`id`)) ENGINE=MyISAM DEFAULT CHARSET=utf8
DROP TABLE IF EXISTS cms_users
CREATE TABLE `cms_users` (  `userid` int(11) NOT NULL AUTO_INCREMENT COMMENT 'ID',  `username` varchar(255) CHARACTER SET utf8 COLLATE utf8_unicode_ci NOT NULL COMMENT '用户名',  `password` varchar(32) CHARACTER SET utf8 COLLATE utf8_unicode_ci NOT NULL DEFAULT '' COMMENT '密码',  `email` varchar(500) NOT NULL,  `regdate` varchar(100) NOT NULL,  `yz` varchar(10) NOT NULL DEFAULT '1',  PRIMARY KEY (`userid`)) ENGINE=MyISAM AUTO_INCREMENT=2 DEFAULT CHARSET=utf8
