DROP TABLE IF EXISTS cms_article
CREATE TABLE `cms_article` (  `id` int(11) NOT NULL AUTO_INCREMENT COMMENT '文章ID',  `cid` int(11) NOT NULL COMMENT '所属栏目ID',  `title` varchar(200) NOT NULL COMMENT '标题',  `subtitle` varchar(200) DEFAULT NULL,  `att` set('a','b','c','d','e','f','g') DEFAULT NULL COMMENT '属性',  `pic` varchar(200) DEFAULT NULL COMMENT '缩略图',  `pic_info` varchar(500) DEFAULT NULL,  `pic02` varchar(500) DEFAULT NULL,  `pic02_info` varchar(500) DEFAULT NULL,  `pic03` varchar(500) DEFAULT NULL,  `pic03_info` varchar(500) DEFAULT NULL,  `source` text COMMENT '来源',  `author` text COMMENT '作者',  `resume` varchar(500) DEFAULT NULL COMMENT '摘要',  `pubdate` varchar(40) NOT NULL COMMENT '发表日期',  `content` text COMMENT '文章内容',  `hits` int(11) NOT NULL DEFAULT '0' COMMENT '点击次数',  `orderlist` int(11) NOT NULL DEFAULT '0',  `created_by` int(11) NOT NULL COMMENT '创建者',  `created_date` datetime NOT NULL COMMENT '创建时间',  `delete_session_id` int(11) DEFAULT NULL COMMENT '删除人ID',  PRIMARY KEY (`id`)) ENGINE=MyISAM AUTO_INCREMENT=17 DEFAULT CHARSET=utf8
INSERT INTO cms_article VALUES('1','1','方塔园',' 方塔园始建于1978年，占地面积182亩，原址为唐宋时期古华亭的闹市中心。园内有国家级、市级、区级文物8处。','','data/attachment/201604/20160419104816_75.jpg','','','','','','','','','2016-04-19 10:46:14','<p>&nbsp;方塔园始建于1978年，占地面积182亩，原址为唐宋时期古华亭的闹市中心。园内有国家级、市级、区级文物8处。整个园景以北宋的方塔为中心，四周环设宋代望仙桥,明代砖雕照壁、兰瑞堂、石雕园,清代天妃宫、陈公祠,还建有何陋轩、其昌廊等。2007年被国家旅游局评定为国家AAAA级旅游景区。</p>','8','0','0','2016-04-22 11:47:38','')
