DROP TABLE IF EXISTS cms_case
CREATE TABLE `cms_case` (  `id` int(11) NOT NULL AUTO_INCREMENT COMMENT '文章ID',  `cid` int(11) NOT NULL COMMENT '所属栏目ID',  `title` varchar(200) NOT NULL COMMENT '标题',  `subtitle` varchar(200) DEFAULT NULL,  `att` varchar(255) DEFAULT NULL COMMENT '属性',  `pic` varchar(200) DEFAULT NULL COMMENT '缩略图',  `pic_big` varchar(200) DEFAULT NULL,  `price` varchar(200) DEFAULT '0' COMMENT '来源',  `brand` varchar(300) DEFAULT NULL COMMENT '作者',  `resume` varchar(500) DEFAULT NULL COMMENT '摘要',  `purpose` text,  `subsidiary` text,  `pubdate` varchar(40) NOT NULL COMMENT '发表日期',  `content` text COMMENT '文章内容',  `hits` int(11) NOT NULL DEFAULT '0' COMMENT '点击次数',  `color` varchar(500) DEFAULT NULL,  `beizhu` text,  `infor01` varchar(500) DEFAULT NULL,  `infor02` varchar(500) DEFAULT NULL,  `infor03` varchar(500) DEFAULT NULL,  `infor04` varchar(500) DEFAULT NULL,  `infor05` varchar(500) DEFAULT NULL,  `infor06` varchar(500) DEFAULT NULL,  `infor07` varchar(500) DEFAULT NULL,  `infor08` varchar(500) DEFAULT NULL,  `infor09` varchar(500) DEFAULT NULL,  `orderlist` int(11) DEFAULT NULL,  `created_by` int(11) NOT NULL COMMENT '创建者',  `created_date` datetime NOT NULL COMMENT '创建时间',  `delete_session_id` int(11) DEFAULT NULL COMMENT '删除人ID',  PRIMARY KEY (`id`)) ENGINE=MyISAM DEFAULT CHARSET=utf8
